import PropTypes from 'prop-types';
import React, { Component } from 'react';
import { BsTrophy } from 'react-icons/bs';
import { FaMedal } from 'react-icons/fa';

export default class RankingCard extends Component {
  switchFunction = (posi) => {
     switch (posi) {
      case 1:
       return <BsTrophy/>;
      case 2:
        return <FaMedal/>
      default:
        return null;
    }

  }
  render() {
    const { picture, key, name, score, position } = this.props;

    return (
      <div>
        <div>
        {this.switchFunction(position)}
        <span id={`${position}`}>{position}</span>
        </div>
        <img src={ picture } alt="gravatar" />
        <span data-testid={ `player-name-${key}` }>{ name }</span>
        <span data-testid={ `player-score-${key}` }>{ score }</span>
      </div>
    );
  }
}

RankingCard.propTypes = {
  key: PropTypes.number.isRequired,
  name: PropTypes.string.isRequired,
  picture: PropTypes.string.isRequired,
  score: PropTypes.number.isRequired,
  position: PropTypes.number.isRequired,
};
